extern char __data_ROM_begin[];		// beginning of .data in ROMextern char __data_begin[];			// beginning of .data in RAMextern char __data_size[];			// .data sizeextern int *__stack_begin;			// start of stack sectionextern int *__bss_begin;            // start of .bss sectionextern int *__bss_end;				// end of .bss sectionextern void main(void);void __init_user(void);asm void __ExitProcess(void);asm void _start(void){	// set sp	lrw     r10, __stack_begin	mov     r0, r10		// Move .BSS to RAM and clear it	lrw     r5, __bss_begin	lrw     r6, __bss_end	cmphs   r5, r6	bt		_end_zero_bss	xor     r4, r4_zero_bss:	subi     r6, 4	st.w     r4, (r6,0)	cmplt    r5, r6	bt       _zero_bss_end_zero_bss:	// Move .Data to RAM	bsr     __init_user			// clear register	movi	r8,0	movi	r9,0	movi	r10,0	movi	r11,0	movi	r12,0	movi	r13,0	movi	r14,0		bsr     main		jmpi	__ExitProcess}asm char *__strcpy(register char *dst, register const char *src){L1:	ld.w	r1,(src,0);		addi	src,4;			st.w	r1,(dst,0);		tst		r1,r1;	addi	dst,4;		bt		L1;	jmp		r15;}asm void *__memcpy(register void *dst, register const void* src, register unsigned long len){	tst		len,len;	bf		L2;L1:	ld.w	r1,(src,0); 	subi    len,4;			st.w    r1,(dst,0);		addi    src,4;			addi    dst,4;			tst		len,len;	bt		L1;	L2:	jmp		r15}void __copy_rom_section(void* dst, const void* src, unsigned long size){    if (size && (dst != src))     {        __memcpy(dst, src, size);    }}void __init_user(void){     __copy_rom_section(__data_begin, __data_ROM_begin, (unsigned long)__data_size);}asm void __ExitProcess(void){_break:  bkpt  bkpt  br _break}