#define BASE  r2#define HANDLER r3#define OFFSET r4asm void Init_Int(register long base,register void(*handler)(), register unsigned char offset){		 mfcr  r1, VBR		 mtcr  BASE,VBR		 movi  r7,31		 cmphs r0,r0 loop:	 		 ld.w  r6,(r1,0)		 st.w  r6,(r2,0)		 addi  r1,4		 addi  r2,4		 declt r7		 bf loop		 		 mfcr  r6,VBR		 lsli  OFFSET,2		 addu  OFFSET,r6		 st.w  HANDLER,(OFFSET,0)		 jmp r15}